context_script:
context.member = frappe.db.get_value('Gym User', {'user_email': frappe.session.user}, 'member')
if context.member:
    context.profile = frappe.db.get_value('Gym Member', context.member, 'pro_image')
    context.member_name = frappe.db.get_value('Gym Member', context.member, 'member_name')
    context.membership = frappe.db.get_all('Gym Membership', filters={'member_name': context.member,'docstatus':1}, fields=['name','member_name','plan','trainer_name','diff_days','status'])
    if context.membership[0].trainer_name:
        context.email = frappe.db.get_value('Gym Trainer', context.membership[0].trainer_name, 'email')
        context.phone = frappe.db.get_value('Gym Trainer', context.membership[0].trainer_name, 'phone')
else:
    log('You are not a member or your member ship is expired')

